import { cardAnatomy } from '@chakra-ui/anatomy';
import { createMultiStyleConfigHelpers } from '@chakra-ui/react';
import { mode } from '@chakra-ui/theme-tools';

const { definePartsStyle, defineMultiStyleConfig } =
	createMultiStyleConfigHelpers(cardAnatomy.keys);

const baseStyle = definePartsStyle((props) => ({
	// define the part you're going to style
	container: {},
	header: {},
	body: {},
	footer: {},
}));

const ghost = definePartsStyle((props) => ({
	container: {
		border: '1px solid whiteAlpha.300',
		borderColor: mode('blackAlpha.300', 'whiteAlpha.700')(props),
		backgroundColor: mode('whiteAlpha.600', 'blackAlpha.600')(props),
		backdropFilter: mode(
			'blur(46.5px) saturate(180%) brightness(1.1) contrast(1.1)',
			'blur(46.5px) saturate(180%) contrast(1.1) brightness(1.1)'
		)(props),
		backgroundBlendMode: 'exclusion',
		boxShadow: 'lg',

		// Add noise to the background
		_after: {
			content: '""',
			position: 'absolute',
			left: 0,
			top: 0,
			right: 0,
			bottom: 0,
			zIndex: -1,
			opacity: mode(0.3, 0.275)(props),
			border: '1px solid',
			borderColor: 'inherit',
			borderRadius: 'inherit',
			background:
				'url(data:image/png;base64,)',
		},

		_before: {
			content: '""',
			position: 'absolute',
			left: 0,
			top: 0,
			right: 0,
			bottom: 0,
			zIndex: -1,
			borderRadius: 'inherit',
			opacity: mode(0.1, 0.5)(props),
			background: mode('whiteAlpha.100', 'gray.700')(props),
		},
	},
	header: {
		backgroundColor: mode('transparent', 'blackAlpha.200')(props),
		borderTopRadius: 'inherit',
	},
	body: {
		backgroundColor: mode('transparent', 'blackAlpha.200')(props),
	},
	footer: {
		position: 'relative',
		bg: mode('whiteAlpha.300', 'blackAlpha.400')(props),
		_before: {
			content: '""',
			position: 'absolute',
			left: 0,
			top: 0,
			right: 0,
			bottom: 0,
			zIndex: -1,
			borderRadius: 'inherit',
			opacity: mode(0.33, 0.1)(props),
			background: mode('whiteAlpha.300', 'gray.700')(props),
		},
		borderBottomRadius: 'inherit',
	},
}));

const window = definePartsStyle((props) => ({
	container: {
		width: '100%',
		border: '1px solid whiteAlpha.300',
		borderColor: 'whiteAlpha.400',
		backgroundColor: mode('whiteAlpha.600', 'blackAlpha.800')(props),
		boxShadow: 'xl',
		backdropFilter: mode(
			'blur(46.5px) saturate(180%)',
			'blur(52.5px) saturate(220%) contrast(1.1) brightness(1.1)'
		)(props),
		zIndex: 1,

		// Add noise to the background
		_after: {
			content: '""',
			position: 'absolute',
			left: 0,
			top: 0,
			right: 0,
			bottom: 0,
			zIndex: -1,
			opacity: mode(0.5, 0.4)(props),
			border: '1px solid',
			borderColor: mode('blackAlpha.400', 'whiteAlpha.400')(props),
			borderRadius: 'inherit',
			background:
				'url(data:image/png;base64,)',
		},

		_before: {
			content: '""',
			position: 'absolute',
			left: 0,
			top: 0,
			right: 0,
			bottom: 0,
			zIndex: -1,
			borderRadius: 'inherit',
			opacity: mode(0.33, 0.2)(props),
			background: mode('whiteAlpha.300', 'gray.600')(props),
		},
	},
	header: {
		pl: 1.5,
		pr: 0,
		py: 0,
		backgroundColor: mode('transparent', 'blackAlpha.200')(props),
		borderTopRadius: 'inherit',
	},
	body: {
		p: 0,
		backgroundColor: mode('transparent', 'blackAlpha.200')(props),
		borderBottomRadius: 'inherit',
	},
	footer: {},
}));

const sizes = {
	xl: definePartsStyle((props) => ({
		container: {
			borderRadius: 'xl',
		},
		header: { padding: 10, borderTopRadius: 'xl' },
		body: {
			paddingX: 20,
		},
		footer: { paddingX: 12, paddingY: 4, borderBottomRadius: 'xl' },
	})),
};

export const cardTheme = defineMultiStyleConfig({
	baseStyle,
	variants: {
		ghost,
		window,
	},
	sizes,
	defaultProps: {
		variant: 'ghost',
	},
});
